import React, { useRef, useState } from 'react';
import { Link } from "react-router-dom";
import { getUserRole } from "../util/auth";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons';

import "../css/categorylist.css"; // Importez votre fichier CSS pour les styles personnalisés

function CategoryList({ category }) {
    const userRole = getUserRole();
    const [scrollPosition, setScrollPosition] = useState(0);
    const containerRef = useRef(null);

    const handleScrollLeft = () => {
        const newPosition = containerRef.current.scrollLeft - 200; // Défilement de 200 pixels à gauche
        containerRef.current.scrollTo({
            left: newPosition,
            behavior: 'smooth' // Défilement fluide
        });
        setScrollPosition(newPosition);
    }

    const handleScrollRight = () => {
        const newPosition = containerRef.current.scrollLeft + 200; // Défilement de 200 pixels à droite
        containerRef.current.scrollTo({
            left: newPosition,
            behavior: 'smooth' // Défilement fluide
        });
        setScrollPosition(newPosition);
    }

    return (
        <div className="category-container">
            <h1>Categories List</h1>
            <div className="category-scroll">
                <button className="scroll-button" onClick={handleScrollLeft} style={{ visibility: scrollPosition > 0 ? 'visible' : 'hidden' }}><FontAwesomeIcon icon={faArrowLeft} /></button>
                <div className="category-list" ref={containerRef}>
                    {category.map((cat) => (
                        <div key={cat.id} className="category-card">
                            <h2>{cat.name}</h2>
                            <img src={`data:image/png;base64,${cat.image}`} alt={cat.name} />
                            {userRole === 'Admin' && (
                                <div className="category-actions">
                                    <Link to={`/editcategory/${cat.id}`} className="btn btn-primary">Edit</Link>
                                    <Link to={`/deletecategory/${cat.id}`} className="btn btn-danger">Delete</Link>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
                <button className="scroll-button" onClick={handleScrollRight} style={{ visibility: containerRef.current && containerRef.current.scrollWidth > containerRef.current.clientWidth + scrollPosition ? 'visible' : 'hidden' }}><FontAwesomeIcon icon={faArrowRight} /></button>
            </div>
        </div>
    );
}

export default CategoryList;
