import React, { useState } from "react";
import { Link } from "react-router-dom";
import { getUserRole } from "../util/auth";
import classes from "../css/categorylist.css";

function CategoryList({ category }) {
  const userRole = getUserRole();
  const [currentIndex, setCurrentIndex] = useState(0);

  const navigateNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === category.length - 1 ? 0 : prevIndex + 1));
  };

  const navigatePrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? category.length - 1 : prevIndex - 1));
  };

  return (
    <>
    <div className="container page-background">
      <div className="container">
        <h1>Categories List</h1>
        <div className={classes["card-container"]}>
          {category.map((cat, index) => (
            <div key={cat.id} className={classes.card} style={{ display: index === currentIndex ? "block" : "none" }}>
              <h3>{cat.name}</h3>
              <img src={`data:image/png;base64,${cat.image}`} alt="" height={200} width={200} />
              {userRole === "Admin" && (
                <div>
                  <Link to={`/editcategory/${cat.id}`} className="m-1 btn btn-primary btn-lg" role="button" aria-disabled="true">
                    Edit
                  </Link>
                  <Link to={`/deletecategory/${cat.id}`} className="m-1 btn btn-danger btn-lg" role="button" aria-disabled="true">
                    Delete
                  </Link>
                </div>
              )}
            </div>
          ))}
        </div>
        <div className={classes.navigation}>
          <div className={classes.arrow} onClick={navigatePrevious}>
            {"<"}
          </div>
          <div className={classes.arrow} onClick={navigateNext}>
            {">"}
          </div>
        </div>
      </div>
      </div>
    </>
  );
}

export default CategoryList;
