import { Link } from "react-router-dom";
import { getUserRole } from "../util/auth";
import "../css/categorylist.css"; // Importez votre fichier CSS pour les styles personnalisés

function CategoryList({ category }) {
    const userRole = getUserRole();

    return (
        <div className="category-container">
            <h1>Categories List</h1>
            {category.map((cat) => (
                <div key={cat.id} className="category-card">
                    <h2>{cat.name}</h2>
                    <img src={`data:image/png;base64,${cat.image}`} alt={cat.name} />
                    {userRole === 'Admin' && (
                        <div className="category-actions">
                            <Link to={`/editcategory/${cat.id}`} className="btn btn-primary">Edit</Link>
                            <Link to={`/deletecategory/${cat.id}`} className="btn btn-danger">Delete</Link>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
}

export default CategoryList;
